import React, {Component} from 'react'
import { Typography, Tabs, Tab, Box, Paper, TextField, Grid, Button} from "@material-ui/core"
import { AddCircleSharp, CreateSharp, SearchSharp, DeleteSharp } from "@material-ui/icons"



class Item extends React.Component {
  constructor(props) {
    super(props);
    this.state = { item: {title : "", userid : "", type : "", sale : ""} , value : 0};
  }

  handleChange = (event, newValue) => {
    this.setState({ value: newValue });
  }

  a11yProps = (index) => {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`,
    };
  }

  render() {
    return (
      <Box>
        <Paper
          style={{ marginLeft: "20%", width : "60%", padding : 20}}
        >
          <Tabs value = {this.value} onChange={this.handleChange} centered>
            <Tab label="제품 추가" icon={<AddCircleSharp />} {...this.a11yProps(0)} />
            <Tab label="제품 검색" icon={<SearchSharp />} {...this.a11yProps(1)} />
            <Tab label="제품 수정"icon={<CreateSharp />} {...this.a11yProps(2)} />
            <Tab label="제품 삭제" icon={<DeleteSharp />} {...this.a11yProps(2)} />
          </Tabs>

        <TabPanel value={this.state.value} index={0} stype = {{padding : 20}}> 

              <br />

              <Grid container>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "title"
                            value = {this.state.item.title}
                          />
                    </Grid>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "userID"
                            value = {this.state.item.title}
                          />
                    </Grid>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "type"
                            value = {this.state.item.title}
                          />
                    </Grid>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "sale"
                            value = {this.state.item.title}
                          />
                    </Grid>

                    <Grid xs = {11} md = {11} item style = {{paddingRight : 16}}>
                        <br></br>
                    </Grid>

                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <Button 
                            style = {{width : "70%"}}
                            color = "secondary" 
                            variant = "contained"
                            onClick = {this.onButtonClick}>
                        작성 완료
                        </Button>
                    </Grid>  
              </Grid>
        </TabPanel>
        <TabPanel value={this.state.value} index={1}>
        <Grid container>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "title"
                            value = {this.state.item.title}
                          />
                    </Grid>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "userID"
                            value = {this.state.item.title}
                          />
                    </Grid>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "type"
                            value = {this.state.item.title}
                          />
                    </Grid>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "sale"
                            value = {this.state.item.title}
                          />
                    </Grid>

                    <Grid xs = {11} md = {11} item style = {{paddingRight : 16}}>
                        <br></br>
                    </Grid>

                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <Button 
                            style = {{width : "70%"}}
                            color = "secondary" 
                            variant = "contained"
                            onClick = {this.onButtonClick}>
                        작성 완료 | 제품 추가
                        </Button>
                    </Grid>  
              </Grid>
        </TabPanel>
        <TabPanel value={this.state.value} index={2}>
            Item Three
        </TabPanel>
        <TabPanel value={this.state.value} index={3}>
            Item Four
        </TabPanel>
        </Paper>

      </Box>
    );
  }
}

class TabPanel extends Component {
  render() {
    return (
      <Typography component="div" hidden={this.props.value !== this.props.index}>
        <Box p={3}>{this.props.children}</Box>
      </Typography>
    );
  }
}

export default Item;
