import React from "react"
import { ListItem, Card, CardActions, CardContent, Typography, IconButton } from "@material-ui/core";
import { DeleteOutlined  } from "@material-ui/icons";

class Item extends React.Component {
    constructor(props) {
        super(props);
        this.state = {item : props.item, setOpen : false};
    }

    handleClickOpen = () => {
        setOpen(true);
      };

    handleClose = () => {
        setOpen(false);
    };

    render() {
        const item = this.state.item;


        return (

            <Card  style = {{width:"20%", height:"25%", justifyContent : 'flex-start', margin : 16, padding : 16}} >
              <CardContent>

                <Typography variant="h5" component="div">
                  {item.title}
                </Typography>

                <Typography
                  sx={{ fontSize: 10 }}
                  color="text.secondary"
                  gutterBottom>
                      type : {item.type}
                </Typography>

                <Typography sx={{ mb: 1.5 }} color="text.secondary">
                    <br />
                  {item.userid}
                </Typography>

              </CardContent>

              <CardActions>
                <IconButton saria-label = "Delete Item"><DeleteOutlined/></IconButton>
              </CardActions>
            </Card>
        );
    }
}

export default Item;
