import React, {Component} from 'react'
import AddItem from './AddItem'
import SearchItem from './SearchItem'
import UpdateItem from './UpdateItem'
import DeleteItem from './DeleteItem'
import { Typography, Tabs, Tab, Box, Paper, TextField, Grid, Button} from "@material-ui/core"
import { AddCircleSharp, CreateSharp, SearchSharp, DeleteSharp, Search} from "@material-ui/icons"



class ItemController extends React.Component {
  constructor(props) {
    super(props);
    this.state = { item: {title : "", userid : "", type : "", sale : ""} , value : 0};
  }

  handleChange = (event, newValue) => {
    this.setState({ value: newValue });
  }

  a11yProps = (index) => {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`,
    };
  }

  onTitleInputChange = (e) => {
    const thisItem = this.state.item;
    thisItem.title = e.target.value;
    this.setState({item : thisItem });
    console.log(thisItem);
  }

  render() {
    return (
      <Box>
        <Paper
          style={{ marginLeft: "20%", width : "60%", height : "350px",padding : 20}} 
        >
          <Tabs value = {this.value} onChange={this.handleChange} centered>
            <Tab label="제품 추가" icon={<AddCircleSharp />} {...this.a11yProps(0)} />
            <Tab label="제품 검색" icon={<SearchSharp />} {...this.a11yProps(1)} />
            <Tab label="제품 수정"icon={<CreateSharp />} {...this.a11yProps(2)} />
            <Tab label="제품 삭제" icon={<DeleteSharp />} {...this.a11yProps(2)} />
          </Tabs>

        <TabPanel value={this.state.value} index={0} stype = {{padding : 20}}> 
            <br />
            < AddItem />
        </TabPanel>

        <TabPanel value={this.state.value} index={1}>
            <br />
            < SearchItem />
        </TabPanel>

        <TabPanel value={this.state.value} index={2}>
            <br />
            < UpdateItem />
        </TabPanel>

        <TabPanel value={this.state.value} index={3}>
            <br />
            <DeleteItem />

        </TabPanel>
        </Paper>

      </Box>
    );
  }
}

class TabPanel extends Component {
  render() {
    return (
      <Typography component="div" hidden={this.props.value !== this.props.index}>
        <Box p={3}>{this.props.children}</Box>
      </Typography>
    );
  }
}

export default ItemController;
