import React from 'react'
import { Box, BottomNavigation, BottomNavigationAction, Paper, Dialog, DialogTitle, DialogContent, DialogContentText, TextField } from "@material-ui/core"
import { AddCircleSharp, CreateSharp, SearchSharp, DeleteSharp } from "@material-ui/icons"



class Item extends React.Component {
  constructor(props) {
    super(props);
    this.state = { item: props.item, open: false };
  }

  handleFormSubmit = (e)=>{
    e.preventDefault()
    this.item().then(()=>{
        this.props.stateRefresh()
    })

    this.setState({open:false})
}

  render() {
    return (
      <Box>
        <Paper
          style={{
            width: "100%",
            height: "70px",
            position: "absolute",
            bottom: 0,
          }}
          color="red"
        >
          <BottomNavigation style={{ justifyContent: "center" }} showLabels>
            <BottomNavigationAction
              label="제품 추가"
              icon={<AddCircleSharp />}
            />
            <Dialog open = {this.item.setOpen} onClose={this.handleClose}>
              <DialogTitle>Subscribe</DialogTitle>
              <DialogContent>
                <DialogContentText>
                  To subscribe to this website, please enter your email address
                  here. We will send updates occasionally.
                </DialogContentText>
                <TextField
                  autoFocus
                  margin="dense"
                  id="name"
                  label="Email Address"
                  type="email"
                  fullWidth
                  variant="standard"
                />
              </DialogContent>
            </Dialog>
            <BottomNavigationAction
              label="제품 검색"
              icon={<SearchSharp />}
              onClick={this.handleClickOpen}
            />
            <BottomNavigationAction
              label="제품 수정"
              icon={<CreateSharp />}
              onClick={this.handleClickOpen}
            />
            <BottomNavigationAction
              label="제품 삭제"
              icon={<DeleteSharp />}
              onClick={this.handleClickOpen}
            />
          </BottomNavigation>
        </Paper>
      </Box>
    );
  }
}

export default Item;
