import './App.css';
import React from 'react';
import ItemController from './ItemController'
import Item from './Item'
import { Grid } from "@material-ui/core";

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items : [
        {userid : 'jeonSoomin', title : 'springT', type : 't-shirt', sale : true },
        {userid : 'jeonSoomin1', title : 'summerT', type : 't-shirt', sale : false }
      ]
    };
  }
  add = (item) => {
    const thisItems = this.state.items;
    item.id = "ID-" + thisItems.length;
    item.sale = false;
    thisItems.push(item);
    this.setState({items : thisItems});
    console.log("items : ", this.state.items)
  }

  delete = (item) => {
    const thisItems = this.state.items;
    console.log("Before Update Items : ", this.state.items)
    const newItems = thisItems.filter(e=> e.title !== item.title);
    this.setState({ items : newItems }, () => {

    })
  }

  render(){
    var Items = this.state.items.length > 0 && (
      <Grid container>
          {this.state.items.map((item, idx) => (
            <Item 
              fullWidth
              item = {item} 
              key = {item.title} />
          ))}
       </Grid>
    )
    return (
      <div className = "App">
        <ItemController add = {this.add} />
        {Items}
      </div>
    )
  }
}

export default App;
