import React from 'react';
import './App.css';
import ItemController from './ItemController'
import Item from './Item'
import ItemRow from './ItemRow'
import { Grid } from "@material-ui/core";
import { call } from "./service/ApiService";

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items : [],
      allitems: []
    };
  }

  componentDidMount(item) {
    call("/shoppingmall/list", "GET", null).then((response) => 
      this.setState({ items : response.data, allitems : response.data})
    )
  }

  loading = () => {
    call("/shoppingmall/list", "GET", null).then((response) => 
        this.setState({ allitems : response.data })
      )
  }

  add = (item) => {
    call("/shoppingmall", "POST", item).then((response) => 
      this.setState({items : response.data, allitems : response.data})
    )
    // this.loading()
  }

  search = (item) => {
    call("/shoppingmall/findByTitle", "POST", item).then((response) =>
      this.setState({items : response.data })
    )
  }

  delete = (item) => {
    call("/shoppingmall", "DELETE", item).then((response) => 
      this.setState({items : response.data, allitems : response.data })
    )
    this.loading()
  }

  update = (item) => {
    this.setState({items : []})
    call("/shoppingmall", "PUT", item).then((response) =>
      this.setState({ items : response.data })
    )
    this.loading()
  }

  render(){
    var Items = this.state.items.length > 0 && (
      <Grid container>
          {this.state.items.map((item, idx) => (
            <Item 
              fullWidth
              item = {item} 
              key = {item.title}
              update = {this.update}
              delete = {this.delete} />
          ))} 
       </Grid>
    )

    var AllItems = this.state.allitems.length > 0 && (
      <table border = "1">
        <thead> 
            <tr>
              <th>sale</th><th>id</th><th>title</th><th>type</th><th>userId</th><th>삭제버튼</th>
            </tr>
        </thead>
          {this.state.allitems.map((item, idx) => (
            <ItemRow 
              fullWidth
              key = {item.title}
              item = {item}
              delete = {this.delete} />
          ))} 
       </table>
    )

    return (
      <div className="App">
        <ItemController
            add={this.add}
            search={this.search}
            update={this.update}
            delete={this.delete}
        />
        {Items}
        {AllItems}
      </div>
    );
  }
}

export default App;
