import React, {Component} from 'react'
import { Typography, Tabs, Tab, Box, BottomNavigation, BottomNavigationAction, Paper } from "@material-ui/core"
import { AddCircleSharp, CreateSharp, SearchSharp, DeleteSharp } from "@material-ui/icons"



class Item extends React.Component {
  constructor(props) {
    super(props);
    this.state = { item: props.item , value : 0};
  }

  handleChange = (event, newValue) => {
    this.setState({ value: newValue });
  }

  a11yProps = (index) => {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`,
    };
  }

  render() {
    return (
      <Box>
        <Paper
          style={{ width: "50%", height: "200px", marginLeft: "25%" }}
          color="red"
        >
          <Tabs
            onChange={this.handleChange}
            aria-label="basic tabs example">
            <Tab label="제품 추가" icon={<AddCircleSharp />} {...this.a11yProps(0)} />
            <Tab label="제품 검색" icon={<SearchSharp />} {...this.a11yProps(1)} />
            <Tab label="제품 수정"icon={<CreateSharp />} {...this.a11yProps(2)} />
            <Tab label="제품 삭제" icon={<DeleteSharp />} {...this.a11yProps(2)} />
          </Tabs>
          {/* <BottomNavigation style={{ justifyContent: "center" }} showLabels>
            <BottomNavigationAction
              label="제품 추가"
              icon={<AddCircleSharp />}
            />
            <BottomNavigationAction
              label="제품 검색"
              icon={<SearchSharp />}
              onClick={this.handleClickOpen}
            />
            <BottomNavigationAction
              label="제품 수정"
              icon={<CreateSharp />}
              onClick={this.handleClickOpen}
            />
            <BottomNavigationAction
              label="제품 삭제"
              icon={<DeleteSharp />}
              onClick={this.handleClickOpen}
            />
          </BottomNavigation> */}

      <TabPanel value={this.state.value} index={0}> 
            Item One
        </TabPanel>
        <TabPanel value={this.state.value} index={1}>
            Item Two
        </TabPanel>
        <TabPanel value={this.state.value} index={2}>
            Item Three
        </TabPanel>
        </Paper>

      </Box>
    );
  }
}

class TabPanel extends Component {
  render() {
    return (
      <Typography component="div" hidden={this.props.value !== this.props.index}>
        <Box p={3}>{this.props.children}</Box>
      </Typography>
    );
  }
}

export default Item;
