import React from 'react'
import { Box, BottomNavigation, BottomNavigationAction, Paper, Dialog, DialogTitle, DialogContent, DialogContentText, TextField } from "@material-ui/core"
import { AddCircleSharp, CreateSharp, SearchSharp, DeleteSharp } from "@material-ui/icons"



class Item extends React.Component {
  constructor(props) {
    super(props);
    this.state = { item: props.item, setOpen: false };
  }

  handleClickOpen = () => {
    console.log("Event !! before click : ", this.state.setOpen);
    this.setState({ setOpen: true }, () => {
      console.log("now opend : ", this.state.setOpen);
    });
  };

  handleClose = () => {
    console.log("Event !! before click : ", this.state.setOpen);
    this.setState({ setOpen: false }, () => {
      console.log("now opend : ", this.state.setOpen);
    });
  };

  render() {
    return (
      <Box>
        <Paper
          style={{
            width: "100%",
            height: "70px",
            position: "absolute",
            bottom: 0,
          }}
          color="red"
        >
          <BottomNavigation style={{ justifyContent: "center" }} showLabels>
            <BottomNavigationAction
              label="제품 추가"
              icon={<AddCircleSharp />}
            />
            <BottomNavigationAction
              label="제품 검색"
              icon={<SearchSharp />}
              onClick={this.handleClickOpen}
            />
            <BottomNavigationAction
              label="제품 수정"
              icon={<CreateSharp />}
              onClick={this.handleClickOpen}
            />
            <BottomNavigationAction
              label="제품 삭제"
              icon={<DeleteSharp />}
              onClick={this.handleClickOpen}
            />
          </BottomNavigation>
        </Paper>
      </Box>
    );
  }
}

export default Item;
