import React, {Component} from 'react'
import { Typography, Tabs, Tab, Box, Paper, TextField, Grid, Button} from "@material-ui/core"
import { AddCircleSharp, CreateSharp, SearchSharp, DeleteSharp } from "@material-ui/icons"



class AddItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = { item: {title : "", userid : "", type : "", sale : ""} , value : 0};
  }

  handleChange = (event, newValue) => {
    this.setState({ value: newValue });
  }

  a11yProps = (index) => {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`,
    };
  }

  onTitleInputChange = (e) => {
    const thisItem = this.state.item;
    thisItem.title = e.target.value;
    this.setState({item : thisItem });
    console.log(thisItem);
  }

  render() {
    return (


              <Grid container>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "title"
                            value = {this.state.item.title}
                            onChange = {this.onTitleInputChange}
                          />
                    </Grid>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "userID"
                            value = {this.state.item.userid}
                          />
                    </Grid>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "type"
                            value = {this.state.item.type}
                          />
                    </Grid>
                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <TextField 
                            style = {{width : "70%"}}
                            placeholder = "sale"
                            value = {this.state.item.sale}
                          />
                    </Grid>

                    <Grid xs = {11} md = {11} item style = {{paddingRight : 16}}>
                        <br></br>
                    </Grid>

                    <Grid xs = {11} md = {11} item style = {{paddingLeft : 20}}>
                        <Button 
                            style = {{width : "70%"}}
                            color = "secondary" 
                            variant = "contained"
                            onClick = {this.onButtonClick}
                            >
                        작성 완료 | 제품 추가
                        </Button>
                    </Grid>  
              </Grid>
    );
  }
}

class TabPanel extends Component {
  render() {
    return (
      <Typography component="div" hidden={this.props.value !== this.props.index}>
        <Box p={3}>{this.props.children}</Box>
      </Typography>
    );
  }
}

export default AddItem;
