import React from 'react';
import './App.css';
import ItemController from './ItemController'
import Item from './Item'
import { Grid } from "@material-ui/core";
import { call } from "./service/ApiService";

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items : [],
      found : {}
    };
  }

  componentDidMount(item) {
    call("/shoppingmall/list", "GET", null).then((response) => 
      this.setState({ items : response.data })
    )
    console.dir(item)
  }

  add = (item) => {
    call("/shoppingmall", "POST", item).then((response) => 
      this.setState({items : response.data})
    )
    console.log("add_items : ", this.state.items)
  }

  search = (item) => {
    call("/shoppingmall/findByTitle", "POST", item).then((response) =>
      this.setState({items : response.data, found : response.data })
    )
    this.render()
    console.log("search_items : ", this.state.items)
  }

  delete = (item) => {
    call("/shoppingmall", "DELETE", item).then((response) => 
      this.setState({items : response.data})
    );
    console.log("delete_items : ", this.state.items)
  }

  update = (item) => {
    call("/shoppingmall", "PUT", item).then((response) =>
      this.setState({ items : response.data })
    );
  }

  render(){
    var Items = this.state.items.length > 0 && (
      <Grid container>
          {this.state.items.map((item, idx) => (
            <Item 
              fullWidth
              item = {item} 
              key = {item.title}
              delete = {this.delete} />
          ))} 
       </Grid>
    )

    return (
      <div className="App">
        <ItemController
          items={this.state.found}
          add={this.add}
          search={this.search}
          update={this.update}
          delete={this.delete}
          fullWidth
        />
        {Items}
      </div>
    );
  }
}

export default App;
